if spark.catalog.tableExists(f"{target_schema}.FactOrder"):
    if len(backdate) == 0:
        last_load_date = spark.sql(f"""
                                SELECT MAX({cdc_col}) FROM {target_schema}.FactOrder
                                """).collect()[0][0]
    else:
        last_load_date = backdate
else:
    last_load_date = '1900-01-01 00:00:00'

last_load_date
